INSERT INTO `news_old` VALUES (11, 1, 'Apple Vision Pro Resmi Diluncurkan di Asia, Termasuk Indonesia', 'Apple resmi meluncurkan Vision Pro di beberapa negara Asia, termasuk Indonesia.', 'Apple resmi meluncurkan Vision Pro di beberapa negara Asia, termasuk Indonesia. Headset berbasis augmented reality (AR) dan virtual reality (VR) ini sebelumnya hanya tersedia di Amerika Serikat sejak awal tahun 2025.\r\n\r\nVision Pro hadir dengan fitur Apple Immersive Display, chip M2 dan R1, serta sistem Eye Tracking canggih yang memungkinkan pengalaman AR/VR yang lebih alami. Apple juga telah bekerja sama dengan beberapa perusahaan teknologi untuk menghadirkan ekosistem aplikasi yang mendukung perangkat ini.\r\n\r\nDi Indonesia, Apple Vision Pro dibanderol mulai dari Rp55 juta dan sudah bisa dipesan melalui Apple Store serta beberapa distributor resmi.', 0, 'Paijo', 'apple-vision-pro-rilis-asia-indonesia', 'published', 'images/news/y47EVLQqzaKpBD3x4yWRGP9gLU6EXJoUjWjJTdfu.jpg', '2025-03-03 09:43:09', '2025-03-03 09:43:09');
